  const [isShown, setIsShown] = useState(false);
  return <Padbox paddingSize={SpaceSizes.xxl}>
      <Stack gap={SpaceSizes.lg}>
        <Paragraph>
          This story demonstrates the accessibility features of the Drawer
          component:
        </Paragraph>
        <ul>
          <li>Focus is automatically trapped within the drawer</li>
          <li>First focusable element receives initial focus</li>
          <li>Proper ARIA attributes for screen readers</li>
          <li>Escape key closes the drawer</li>
          <li>Focus is restored when drawer closes</li>
        </ul>

        <Button onClick={() => setIsShown(true)}>Open Accessible Drawer</Button>

        {/* Background focusable elements to test focus trap */}
        <input placeholder="This should not receive focus when drawer is open" />
        <Button>This button should also be unreachable</Button>

        {isShown && <Drawer footer={<Inline gap={SpaceSizes.md} justify="flex-end">
                <Button variant="outline" onClick={() => setIsShown(false)}>
                  Cancel
                </Button>
                <Button>Confirm</Button>
              </Inline>} title="Accessibility Demo" onClose={() => setIsShown(false)}>
            <Stack gap={SpaceSizes.md}>
              <Paragraph>
                Focus should automatically move to the first input below when
                the drawer opens:
              </Paragraph>
              <input aria-label="Search input" placeholder="First focusable element (gets auto-focus)" />
              <input aria-label="Filter input" placeholder="Second input" />
              <Button>Focusable button</Button>
              <Paragraph>
                Try pressing Tab to navigate through focusable elements. Focus
                should remain trapped within this drawer. Press Escape to close
                the drawer.
              </Paragraph>
            </Stack>
          </Drawer>}
      </Stack>
    </Padbox>;
