function CardHeaderTemplate({
  title = 'Card Title',
  subtitle = 'Card Subtitle',
  titleLinesCount = undefined,
  subtitleLinesCount = undefined,
  onHelpClick = null,
  helpTooltip = null,
  leftAdornment = (
    <div style={{ marginLeft: '-8px' }}>
      <CardIconButton aria-label="Drag Card">
        <Icon color="neutral.800" name="grip-dots-vertical" />
      </CardIconButton>
    </div>
  ),
}) {
  return (
    <CardHeader
      actions={actions}
      helpTooltip={helpTooltip}
      leftAdornment={leftAdornment}
      maxSubtitleLinesCount={subtitleLinesCount}
      maxTitleLinesCount={titleLinesCount}
      subtitle={subtitle}
      title={title}
      onHelpClick={onHelpClick}
    />
  );
}

function CardHeaderTemplateWithAdornment() {
  return (
    <CardHeader
      actions={actions}
      leftAdornment={<UserAvatar label="AB" />}
      subtitle="Card subtitle"
      title="Card title"
    />
  );
}

function CardContentTemplate() {
  return (
    <CardContent>
      <Text size="md">
        Etiam id laoreet tellus. Pellentesque interdum porttitor iaculis. Ut leo
        urna, lobortis ac fermentum at, efficitur vel lorem. Cras viverra tempor
        augue, sed rutrum neque vestibulum at.
      </Text>
    </CardContent>
  );
}

function CardActionsTemplate({
  actions: actionsFromProps = actions,
}: {
  actions?: CardActionsProps['actions'];
}) {
  return (
    <CardActions
      actions={actionsFromProps}
      rightAdornment={
        <div style={{ marginRight: '-8px' }}>
          <CardIconButton aria-label="Resize Card">
            <Icon color="neutral.700" name="resize" />
          </CardIconButton>
        </div>
      }
    />
  );
}

function CardMediaTemplate({
  width,
  height = '15rem',
}: {
  width?: string;
  height?: string;
}) {
  return <CardMedia alt="test" mediaSrc={image} style={{ height, width }} />;
}

